import React, {useState, useEffect} from 'react'
import Task3A from './Task3A'
import Task3B from './Task3B'

// conditional Rendering
function Task3() {

    const [isSubscribed, setSubscribed] = useState(false)
    const [isUnsubscribed, setUnSubsribed] = useState(false)

    // Cause Side Effect
    useEffect(() => {
        console.log("Is Subscribed: " + isSubscribed)
        console.log("Is UnSubscribed: " + isUnsubscribed)
    }, [isSubscribed, isUnsubscribed])
    
    const handleSubscribe = () => {
        setSubscribed(true)
    }

    const handleUnSubscribe = () => {
        setUnSubsribed(true)
    }

  return (
    <>
        {isSubscribed ? <Task3A /> : <Task3B />}
        <button onClick={handleSubscribe} className={isSubscribed ? 'false' : 'hide'}>Subscribe</button>
        <button onClick={handleUnSubscribe}>UnSubscribe</button>
    </>
  )
}

export default Task3
