function Comments({inputs}) {

    const {bookInput, authorInput, genre, status} = inputs
    
    const placeholder = 'Enter Your Review here...'
    const handleSubmit = () => {
        console.log()
    }
  return (
    <>
        <h1>Book Reviews</h1>
        <select>
            {bookInput.map((bname, index) => (
                <option key={index} value={bname}>{bname}</option>
            ))}
            
            
        </select> <br /> <br />
        <textarea rows='20' cols='50' placeholder={placeholder}></textarea>
        <button onClick={handleSubmit}>Submit</button>
    </>
  )
}

export default Comments
